import React from 'react'
import { useEffect } from 'react';
import { useState } from 'react';
import Form from '../components/Form'
import ListGroup from '../components/ListGroup'
import './App.css';
import Notas from '../components/Notas';

export default function Index() {

  const [notas, setNotas] = useState([])
  const getNotas = async () => {
    const response = await fetch('http://localhost:5000/api/notas')
    const result = await response.json()
    setNotas(result)
  }
  useEffect(() => {
    getNotas();

  }, [notas])

  const deleteNotas = async(id) => {
    //await fetch('http://localhost:5000/api/notas/'+id)
    console.log(id)
  }


  return (
    <div className='content-app'>
      <header className='content-app-title'>
        <h1>Carpetas</h1>
        <strong>80 carpetas</strong>

      </header>
      <div className='row'>
        <div className='col-sm-12 col-md-4'>
          <Form />
        </div>
        <div className='col-sm-12 col-md-8'>
          <ListGroup>
            {notas.map((nota, index) => (
              <Notas key={index} deleteNotas={deleteNotas} id={nota._id} title={nota.title} content={nota.content} />
            ))}
          </ListGroup>
        </div>
      </div>
    </div>
  )
}
